mapper.update:
UPDATE posts
SET view_count = view_count + 1
WHERE id = #{id}

mapper.select:
SELECT p.*, u.nickname as author_name, u.avatar as author_avatar, (SELECT COUNT(*) FROM comments c WHERE c.post_id = p.id) as comment_count, (SELECT COUNT(*) FROM likes l WHERE l.post_id = p.id) as like_count FROM posts p LEFT JOIN users u ON p.author_id = u.id WHERE p.author_id IN
#{userId}
ORDER BY p.created_at DESC
